import { MultiCallABI } from '../constants';
import { BigNumber } from '@ethersproject/bignumber';
import { useChainCall } from './useChainCalls';
import { useMulticallAddress } from './useMulticallAddress';
var GET_CURRENT_BLOCK_TIMESTAMP_CALL = MultiCallABI.encodeFunctionData('getCurrentBlockTimestamp', []);
var GET_CURRENT_BLOCK_DIFFICULTY_CALL = MultiCallABI.encodeFunctionData('getCurrentBlockDifficulty', []);
export function useBlockMeta() {
  var address = useMulticallAddress();
  var timestamp = useChainCall(address && {
    address: address,
    data: GET_CURRENT_BLOCK_TIMESTAMP_CALL
  });
  var difficulty = useChainCall(address && {
    address: address,
    data: GET_CURRENT_BLOCK_DIFFICULTY_CALL
  });
  return {
    timestamp: timestamp !== undefined ? new Date(BigNumber.from(timestamp).mul(1000).toNumber()) : undefined,
    difficulty: difficulty !== undefined ? BigNumber.from(difficulty) : undefined
  };
}
